script:
frappe.ui.form.on('Interview Feedback', {
    onload(frm) {
        if (frm.is_new()) {
            var competencies = [
                "Functional / Subject Knowledge",
                "Vision & Strategy",
                "Business Acumen",
                "Teamwork & Collaboration",
                "Develop Self & Team",
                "Administrative/Managerial quality",
                "Customer Focus",
                "Innovates & Improves",
                "Get Results",
                "Communicate to Influence & Engage",
                "Safety & Environment awareness"
            ];
            var characteristics = [
                "Drive to Achieve",
                "Learning Agility",
                "Intellectual Capability",
                "Managing Ambiguity",
                "Emotional Maturity"
            ];

            var existing_competencies = frm.doc.custom_ratings.map(function(row) {
                return row.competency;
            });

            for (var i = 0; i < competencies.length; i++) {
                if (!existing_competencies.includes(competencies[i])) {
                    var row = frm.add_child("custom_ratings");
                    row.competency = competencies[i];
                    if (i < characteristics.length) {
                        row.personal_characteristics = characteristics[i];
                    }
                }
            }
            frm.refresh_field("custom_ratings");
        }
    }
});

frappe.ui.form.on('Job Opening', {
	designation:function(frm) {
		var plan = frm.doc.staffing_plan;
		var desi = frm.doc.designation;
		var req = frm.doc.job_requisition;
		if (desi){
		    frm.set_value('job_title',desi);
    		frm.refresh_field('job_title');
		}
		if (plan){
    		var title = plan+"-"+desi;
    		frm.set_value('__newname',title);
    		frm.refresh_field('__newname');
		} else if(req) {
		    var title = req+"-"+desi;
    		frm.set_value('__newname',title);
    		frm.refresh_field('__newname');
		}
	}
})

frappe.ui.form.on('Employee', {
    probation_period: function(frm) {
        var doj = frm.doc.date_of_joining;
        var probation_period = frm.doc.probation_period;

        if (doj && probation_period) {
            var training_probation_days = probation_period * 60;
            var end_date = frappe.datetime.add_days(doj, training_probation_days);
            var formatted_date = frappe.datetime.str_to_user(end_date);

            frm.set_value('probation_period_end_date', formatted_date);
            frm.refresh_field('probation_period_end_date');
        }
    },
    date_of_joining: function(frm) {
        var doj = frm.doc.date_of_joining;
        var probation_period = frm.doc.probation_period;

        if (doj && probation_period) {
            var training_probation_days = probation_period * 60;
            var end_date = frappe.datetime.add_days(doj, training_probation_days);
            var formatted_date = frappe.datetime.str_to_user(end_date); 

            frm.set_value('probation_period_end_date', formatted_date);
            frm.refresh_field('probation_period_end_date');
        }
    }
});

frappe.ui.form.on('Job Applicant', {
	onload:function(frm) {
		var interview_round = frm.doc.custom_interview_round;
		var interview_status = frm.doc.custom_interview_status;

		var interview_round_list = ['HR Round', 'Department Round', 'Management Round', 'General Round'];
		var job_offer_list = ['Job Offer'];


		if (interview_round_list.includes(interview_round))
		{
		    frm.set_df_property('custom_interview_status', 'options', ['Pending', 'Under Review', 'Cleared', 'Rejected', 'Not Appeared']);
		}
		else if (job_offer_list.includes(interview_round))
		{
		    frm.set_df_property('custom_interview_status', 'options', ['Awaiting Response', 'Accpeted', 'Rejected']);
		}

	},

	custom_interview_round:function(frm) {
		var interview_round = frm.doc.custom_interview_round;
		var interview_status = frm.doc.custom_interview_status;

		var interview_round_list = ['HR Round', 'Department Round', 'Management Round', 'General Round'];
		var job_offer_list = ['Job Offer'];


		if (interview_round_list.includes(interview_round))
		{
		    frm.set_df_property('custom_interview_status', 'options', ['Pending', 'Under Review', 'Cleared', 'Rejected', 'Not Appeared']);
		}
		else if (job_offer_list.includes(interview_round))
		{
		    frm.set_df_property('custom_interview_status', 'options', ['Awaiting Response', 'Accpeted', 'Rejected']);
		}

	}
});

frappe.ui.form.on('Staffing Plan', {
    refresh:function(frm) {

        var name = frm.doc.name; 


        frm.doc.staffing_details.forEach(detail => {
            var designation = detail.designation;
            var child_name = detail.name;
            console.log(name);
            console.log(designation);

            frappe.db.get_list('Job Opening', {
                fields: ['custom_hired_vacancy'],
                filters: {
                    designation: designation,
                    staffing_plan: name
                },
            }).then(result => {
                if (result.length > 0) {
                    var customHiredVacancy = result[0].custom_hired_vacancy;
                    console.log(customHiredVacancy);

                    frappe.db.set_value('Staffing Plan Detail', child_name, 'custom_hired_vacancy', customHiredVacancy);
                    frm.refresh_field('custom_hired_vacancy');

                }
            });
        });
    }
});

