frappe.ui.form.on('Full and Final Statement', {
    refresh: function(frm) {
        // Flag to prevent multiple alerts
        frm.messageShown = false;

        var a = frm.doc.date_of_joining;
        var b = frm.doc.relieving_date;

        // Function to format date as dd-mm-yyyy
        function formatDate(dateString) {
            var date = new Date(dateString);
            var day = String(date.getDate()).padStart(2, '0');
            var month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
            var year = date.getFullYear();
            return `${day}-${month}-${year}`;
        }

        // Function to calculate year difference as a float
        function calculateYearDifference(date1, date2) {
            var d1 = new Date(date1);
            var d2 = new Date(date2);
            var diffInMilliseconds = d2 - d1;
            var diffInYears = diffInMilliseconds / (1000 * 60 * 60 * 24 * 365.25); // Considering leap years
            return parseFloat(diffInYears.toFixed(1)); // Rounded to 1 decimal place
        }

        if (a && b) {
            var formattedDateOfJoining = formatDate(a);
            var formattedDateOfLeaving = formatDate(b);

            // Calculate the year difference
            var yearDifference = calculateYearDifference(a, b);

            // Set the total_years_of_experience field with the year difference
            frm.set_value('custom_total_service_period', yearDifference);
            frm.refresh_field('custom_total_service_period');
        }
    },

    employee: function(frm) {
        var payables = ['Salary Slip', 'Gratuity', 'Bonus', 'Leave Encashment'];
        var payables_doctype = ['Salary Slip', 'Employee Gratuity', 'Retention Bonus', 'Leave Encashment'];
        var receivables = ['Loan', 'Advance'];
        var receivables_doctype = ['Loan', 'Employee Advance'];


        frm.clear_table('payables'); 
        frm.clear_table('receivables'); 
        frm.clear_table("custom_asset_allocation");


        payables.forEach(function(item, index) {
            var row = frm.add_child('payables'); 
            row.component = item; 
            row.reference_document_type = payables_doctype[index]; 
        });


        receivables.forEach(function(item, index) {
            var row = frm.add_child('receivables'); 
            row.component = item; 
            row.reference_document_type = receivables_doctype[index]; 
        });


        frm.refresh_field('payables'); 
        frm.refresh_field('receivables'); 

        if (frm.doc.employee){
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Employee",
                    name: frm.doc.employee,
                    filters: {
                        fields: ["custom_allocated_asset_employee"]
                    }
                },
                callback: function(response) {
                    if(response.message) {

                        $.each(response.message.custom_allocated_asset_employee || [], function(i, row) {

                            let new_row = frm.add_child("custom_asset_allocation");
                            new_row.asset = row.asset; 
                            new_row.department = row.department;
                            new_row.model_no = row.model_no; 
                            new_row.status = row.status; 
                            new_row.allocation_date = row.allocation_date;
                            new_row.returned_date = row.returned_date;
                        });


                        frm.refresh_field("custom_asset_allocation");
                    }
                }
                });
        }

    },
    before_save:function(frm){
        var payable = frm.doc.total_payable_amount;
        var receivable = frm.doc.total_receivable_amount;

        var net_pay = payable-receivable;

        frm.set_value('custom_net_payable_amount', net_pay);
        frm.refresh_field('custom_net_payable_amount');

    }

});

frappe.ui.form.on('Exit Questionnaire', {
    employee: function(frm) {
        var emp = frm.doc.employee;
        var emp_nm = frm.doc.employee_name;

        if (emp) {
            frappe.db.get_value('Employee', emp, 'relieving_date').then(function(res) {
                var relieving_date = res.message.relieving_date;

                if (!relieving_date) {
                    var message = __("Please set relieving date in Employee: {0} - {1}", [emp, emp_nm]);
                    frappe.throw(message);
                }
            });
        }
    }
});
