css:
h2, h3{
    text-align: center;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
}
h3{
    padding-top: 10px !important;
}
.print-format{
    padding: 30px;
}

.sectiondetail{
    margin-top: 15px;
    font-size: 15px !important;
    line-height: 1.5; 
} 
.sectionmargin th{
    width: 33.33%;
}

.print-format td, .print-format th {
    padding: 5px !important;
    font-size: 15px !important;
    color: black !important;
}

.print-format th {
    font-weight: bold;
}

.footer-right {
    margin-top: 50px !important;
    text-align: right;
    font-size: 15px !important;
}




.row {
    display: flex;
    justify-content: space-between; 
}
.form-row {
    display: flex;
    justify-content: space-between;
}
.form-column {
    flex: 1%; 
    padding: 5px;
    padding-top:0px;
    box-sizing: border-box;
    border: 1px solid gray;
}
.form-column1 {
    border-left: 0px solid gray;
}

.form-column p {
    margin: 10px 0; 
}

.sectionmarginfirst{
    margin-top: 15px;
    font-size: 15px !important;
    line-height: 1; 
}


.print-format td, .print-format th {
    padding: 5px !important;
    font-size: 15px !important;
    color: black !important;
}

.print-format th {
    font-weight: bold;
}
.detail{
    line-height: 1.5 !important; 
    padding: 2px 5px;
    box-sizing: border-box;
    border: 1px solid gray;
    border-top: 0px;
}
.detailbackground{
    line-height: 1.5 !important; 
    padding: 5px 5px;
    box-sizing: border-box;
    border: 1px solid gray;
    border-top: 0px;
    border-bottom: 0px;
}
.sectionmargin{
    padding: 0px 6px;
    margin-bottom: 5px !important;
}

.sectionmargin table{
    border: 1px solid #d1d8dd !important;
}
