css:
h2, h3{
    text-align: center;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
    font-weight: normal !important;
    padding: 4px 0px !important;
}
.print-format{
    padding: 30px !important;
    font-size: 16px !important;
    line-height: 1.5 !important;
}
.text-right{
    text-align: right;
    padding: 4px 0px !important;
}
.grid-container {
  display: flex;
  flex-wrap: wrap; /* Allow items to wrap to the next line */
  padding: 20px 0px 0px 0px !important;
  line-height: 2 !important;
}
.grid-container > div {
  width: 50%;
}
.grid-containersecond {
  display: flex;
  flex-wrap: wrap; /* Allow items to wrap to the next line */
  padding: 80px 0px 30px 0px !important;
  line-height: 1.5 !important;
}
.grid-containersecond > div {
  width: 33.33%;
}

.fixed-height{
    height: auto !important;
    padding-top: 5px 0px !important;
    line-height: 1.4 !important;
    margin: 8px 0px 16px !important;
}
.fixed-heightleft{
    padding-right: 15px !important;
    padding-top: 2rem 0px !important;
    line-height: 1.4 !important;
    margin: 8px 0px 16px !important;
}
.paragraph{
    margin: 20px 0px !important;
}


.notes{
    line-height: 1.4 !important;
    font-size: 14px !important;
}

.underline {
    position: relative;
    display: inline-block;
}

.underline::after {
    content: '';
    position: absolute;
    left: 0;
    bottom: 1px; /* Adjust this value to control the space between the text and the underline */
    width: 100%;
    height: 1px; /* Adjust the thickness of the underline here */
    background: black; /* Adjust the color of the underline here */
}
