css:
h2{
    text-align: center;
    text-transform: uppercase;
    margin: 8px 0px 13px 0px !important;
}
* {
  box-sizing: border-box;
}
.print-format{
    font-size: 16px;
    line-height: 1.8 !important;
    padding: 0.3in 0.4in 0.1in !important;
    /*margin: 20px 10px !important;*/
    /*border: 1px solid #36414c;*/
    /*border-radius: 8px !important;*/
}
.grid-container {
  display: flex;
  flex-wrap: wrap; /* Allow items to wrap to the next line */
  padding: 20px 0px 0px 0px !important;
  line-height: 2 !important;
}
table {
  border: 1px solid #36414c; /* Set border color */
}
.grid-container > div {
  width: 50%;
}
.address{
    padding: 5px 0px 0px !important;
}
.grid-containersecond {
  display: flex;
  flex-wrap: wrap; /* Allow items to wrap to the next line */
  padding: 5px 0px 0px 0px !important;
  line-height: 1.8 !important;
}

.grid-containersecond > div {
  width: 25%;
}
.sectiontable{
    margin: 1.5rem 0rem 0rem !important;
}
.print-format td, .print-format th {
    padding: 6px 4px !important; 
}
.total{
    background-color: #f4f5f6;
}
.authenticate{
    margin-top: 30px !important;
    line-height: 1.8 !important;
}
.witness{
  padding: 20px 0px 0px 0px !important;
  line-height: 1.5 !important;
}
h3{
    margin-bottom: 2px !important;
}
.signature{
    padding: 70px 0px 0px 0px !important;
}
.grid-containerthird {
  display: flex;
  justify-content: space-between; /* Aligns items with equal space between them */
}

.left {
  text-align: left;
}

.center {
  text-align: center;
}

.right {
  text-align: right;
}
