css:
.sectionmargin {
    margin-left: 5%;
    margin-right: 5%;

}
.print-format td {

    padding: 5px !important;
}
.flex-container {
        display: flex;
        justify-content: space-between;
    }
    .flex-container .left-side {
        text-align: left;
    }
    .flex-container .right-side {
        text-align: right;
    }

.print-format .row:not(.section-break) {
    line-height:1.6;
    margin-top: 0px !important;
}
@media (min-width: 768px){
.container {
    width: 500px;
    height: 20px;
}
}
