css:
.font{
    font-size: 17px;
}

h2,h4{
    text-align: center;
}
.print-format{
    padding: 30px;
}
.row {
    display: flex;
    justify-content: space-between; 
}
.form-row {
    display: flex;
    justify-content: space-between;
}

.form-column {
    flex: 1; 
}

.form-column p {
    margin: 10px 0; 
}

.sectionmarginfirst{
    margin-top: 5px;
    line-height: 1; 
}

.sectionmargin{
    margin-top: 15px;
}

.print-format td, .print-format th {
    padding: 5px !important;
    color: black !important;
}

.print-format th {
    font-weight: bold;
}

.table-cell-label {
    width: 40%;
}
.table-cell-value {
    width: 60%;
}

.title{
    font-weight: bold;
}

.linespace {
    line-height: 1.2;
    margin-bottom: 8px;
}


th:first-child, th:nth-child(3) {
    width: 40%;
    text-align: left;
}
th:nth-child(2), th:nth-child(4) {
    width: 10%;
    text-align: left;
}
.left-align {
    text-align: left !important;
}
.right-align {
    text-align: right;
}

.sectioncomment, .sectionsalary, .sectionsignature{
    margin-top: 25px;
}

.sectionsalary th{
    width: 20%;
}

.sectionsignature td {
    width: 50%;
}

.sectionsignature span{
    line-height: 1.5; 
}

.page-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.footer-left {
    text-align: left;
}

.footer-right {
    text-align: right;
}


.sectionmargin {
    margin: 20px;
}
.linespace {
    margin-bottom: 20px;
}

.rating-scale {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}
.rating-scale p {
    flex: 0 0 48%;
    margin: 0;
}
