css:
html, body {
    height: 100%;
    margin: 0;
}

.container {
    display: flex;
    flex-direction: column;
    min-height: 100vh;
    box-sizing: border-box;
}
.footer {
   position: fixed;
   left: 0;
   bottom: 0;
   width: 100%;
   background-color: red;
   color: white;
   text-align: center;
}
h2 {
    text-align: center;
    font-weight: bold !important;
    margin-bottom: 20px !important;
}

.print-format {
    padding: 0.30in !important;
    font-size: 15px !important;
}

.print-format td, .print-format th {
    padding: 5px !important;
}

th, td {
    border: 1px solid black !important;
    text-align: left;
    word-wrap: break-word;
}

th {
    color: black !important;
    font-weight: bold !important;
}

td {
    width: 50%;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin: 1rem 0px;
    font-size: 15px !important;
    table-layout: fixed;
}
.name {
    margin-bottom: 5px !important;
}

.signatures {
    padding: 20px 0;
    position: fixed;    
       bottom: 0px; 
       width: 100%;
}
div.relative {
  position: relative;
  width: 400px;
  height: 200px;
  border: 3px solid #73AD21;
} 

div.absolute {
  position: absolute;
  top: 80px;
  right: 0;
  width: 200px;
  height: 100px;
  border: 3px solid #73AD21;
}
.signatures .first {
    margin-bottom: 100px; 
}
